<script type=""text/javascript"">
    var _employees = [];
    $(document).ready(function () {
        LoadEmployees();
    });
    function LoadEmployees() {
        Reset();
        $.get(""../Employees/GetEmployees"", function (emps) {
            _employees = emps;
            $.map(emps, function (emp) {
                var tempStr = ""<tr>"";
                tempStr += ""<td>"" + emp.id + ""</td>"";
                tempStr += ""<td>"" + emp.name + ""</td>"";
                tempStr += ""<td>"" + emp.password + ""</td>"";
                tempStr += ""<td>"" + emp.kills + ""</td>"";
                tempStr += ""</tr>"";
                $(""#tblEmployee tbody"").append(tempStr);
            });
        });
    }
    function Reset() {
        _employees = [];
        $(""#tblEmployee tbody tr"").remove();
        $("".inputField"").val("""");
    }
    function Save() {
