        });
    }

    function Edit(empId) {
        var emp = _employees.find(x => x.id == empId);
        $(""#txtEmpId"").val(emp.id);
        $(""#txtName"").val(emp.name);
        $(""#txtpassword"").val(emp.password);
        $(""#txtKills"").val(emp.kills);
    }
